mapper.insert:
INSERT INTO RTP_POOLS (
mrws_id, rtp_local, rtp_zone
)
VALUES (
#{mrws_id}, #{rtp_local}, #{rtp_zone}
)

mapper.update:
 UPDATE RTP_POOLS SET mrws_id = #{rtpPools.mrws_id},
		rtp_local = #{rtpPools.rtp_local}, rtp_zone = #{rtpPools.rtp_zone}
		WHERE r_cnt IN 
			#{r_cnt}

mapper.delete:
 DELETE FROM RTP_POOLS
		WHERE r_cnt IN 
			#{r_cnt}
